-- BLOCK 1: Create database (run, then switch 'Connect to' dropdown to fraud_demo) 

CREATE DATABASE fraud_demo; 



-- BLOCK 2: Master key and credentials 

CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'Demo@Password123!'; 



CREATE DATABASE SCOPED CREDENTIAL adls_cred 

    WITH IDENTITY = 'Managed Identity'; 



CREATE EXTERNAL DATA SOURCE adls_raw 

    WITH ( LOCATION = 'abfss://raw@kals85swsdatalake.dfs.core.windows.net/transactions/', 

           CREDENTIAL = adls_cred ); 



CREATE EXTERNAL FILE FORMAT csv_fmt 

    WITH ( FORMAT_TYPE = DELIMITEDTEXT, 

           FORMAT_OPTIONS (FIELD_TERMINATOR = ',', FIRST_ROW = 2) ); 



